import * as React from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { addUser } from "../../store/Challenge4/users/action";
import showAlert from "./hoc/hocAlert";
import {
  genericAlert,
  removeAlerts
} from "../../store/Challenge4/alerts/action";

export interface CreateUserProps {
  addUser?: (user_name: string, address: string, age: number | string) => void;
  genericAlert?: (text: string, type: string) => void;
  removeAlerts?: () => void;
  history?: any;
}

export interface InternalState {
  user_name: string;
  address: string;
  age: number | string;
}

class CreateUser extends React.Component<CreateUserProps, InternalState> {
  constructor(props: CreateUserProps) {
    super(props);
    this.state = {
      user_name: "",
      address: "",
      age: ""
    };
  }

  handleChange = (evt: React.ChangeEvent<HTMLInputElement>): void => {
    this.setState({
      ...this.state,
      [evt.target.name]: evt.target.value
    });
  };

  checkAdd = (): void => {
    const { user_name, address, age } = this.state;
    const { addUser, genericAlert } = this.props;
    if (user_name === "" || address === "" || age === "") {
      genericAlert &&
        genericAlert(
          "Please make sure you fill the form before submitting it...",
          "warning"
        );
    } else {
      try {
        addUser && addUser(user_name, address, age);
        genericAlert &&
          genericAlert("New user created with success!!", "success");
      } catch {
        genericAlert && genericAlert("Something went wrong", "danger");
      }
    }
  };

  render(): JSX.Element {
    const handleChange = this.handleChange;
    const { removeAlerts } = this.props;
    const { user_name, address, age } = this.state;
    return (
      <div className="container shadow">
        <h3 className="m-3">Add a new user!</h3>
        <button
          className="btn btn-outline-dark col-3 mb-3"
          onClick={(): void => {
            removeAlerts && removeAlerts();
          }}
        >
          Clear all alerts
        </button>
        <div className="form-group ">
          <div className="row border p-2 shadow">
            <label className="col-2 ml-2 mt-3 mb-3 mr-1">User name:</label>
            <input
              name="user_name"
              value={user_name}
              onChange={handleChange}
              type="text"
              className="form-control col-9 m-3 "
              aria-describedby="helpId"
              placeholder="Type some text"
            ></input>

            <label className="col-2 ml-2 mt-3 mb-3 mr-1">Address:</label>
            <input
              name="address"
              value={address}
              onChange={handleChange}
              type="text"
              className="form-control col-9 m-3 "
              aria-describedby="helpId"
              placeholder="Type some text"
            ></input>

            <label className="col-2 ml-2 mt-3 mb-3 mr-1">Age:</label>
            <input
              name="age"
              value={age}
              onChange={handleChange}
              type="number"
              className="form-control col-9 m-3"
              aria-describedby="helpId"
              placeholder="User Age"
            ></input>

            <button
              onClick={(): void => {
                this.checkAdd();
              }}
              type="button"
              className="btn btn-outline-dark mb-3 ml-5 col-1"
            >
              Submit
            </button>
          </div>
        </div>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch: any): any =>
  bindActionCreators({ addUser, genericAlert, removeAlerts }, dispatch);

export default connect(
  null,
  mapDispatchToProps
)(showAlert(CreateUser));
